            setpopularCity(cityName)


        }).catch((err)=>{ console.log(err) })
    }

    const object = Object.values(popularCity)
    let cityName = ''
    for (const value of object) { cityName = cityName +' '+ value }


      const showFiveCityPop = popularCity && displayCity ? (<span style={{color:'red'}}> {cityName} </span>) : null
      console.log(displayCity)


    return (



        <div className={main.mainForm}>
                <div className={main.formLeft} onClick={()=> setDisplayCity(!displayCity)}>
                    <div className={main.inputBox}>
                                <input id='login' type='text' 
                                       placeholder='Départ' onClick={popularCityFive} 
                                />
                    </div>
                    {showFiveCityPop}
                    <div className={main.inputBox}>
                                <input id='login' type='text' 
                                placeholder='Arrivée'/>
                    </div>
                    <div>
                            <button className='btn btn-secondary btn-lg btn-block'>Rechercher</button>
                    </div>
            </div>


            <div className={main.containerRight}>

            </div>


    </div>
    )
}

export default MainForm
