import React,{useState,useEffect } from 'react';
import Header from '../Home/Header/Header'
import MainTitle from '../Home/MainTitle/MainTitle'
import MainForm from '../Home/MainForm/MainForm'
import home from './home.module.css'

const Home = () => {

    const [show, setShow] = useState(false);

        useEffect(() => {
            let visited = localStorage.getItem('premiereVisite');
            if(visited) {
                setShow(false)
            } else {
                 localStorage.setItem('premiereVisite',true);
                setShow(true)
            }
        },[])

    return(

        <div className={home.home}>
            <Header />
            <MainTitle />
            <MyModal click={()=>setShow(false)} modal={show} />
            <MainForm />
        </div>
    )
}



export default Home;
